<script>
    document.addEventListener("DOMContentLoaded", function(e) {
        const addImage = () => {
            const menu = document.querySelector('nav>ul>li>ul.sub-menu');
            menu.insertAdjacentHTML('beforeend', '<picture><source srcset="<?php echo get_template_directory_uri()?>/assets/img/menu-image.webp" type="image/webp"><img src="<?php echo get_template_directory_uri()?>/assets/img/menu-image.png" class="submenu-image" alt="menu-image"></picture>')
        };
        addImage();
        const openMenu = () => {
          document.addEventListener('click', function (e) {
              let target = e.target;
                if(target.closest('.menu-item-has-children')) {
                    target.classList.toggle('open');
                }
                if(target.closest('.header__burger')) {
                    target.parentElement.classList.toggle('open');
                    document.querySelector('.header__nav-wrap').classList.toggle('open');
                }
          });
        };
        openMenu();
    });
</script>
